jQuery(document).ready(function($){
   	/****************************************************************
   		disable click/touchevent on scrolling, currently only invoked
   		for wppizza-add-to-cart, but could be used with others
   		only for iStuff - hence the wrapper 
   		(as Android is just fine without it)
   	****************************************************************/
	if((navigator.userAgent.match(/iPhone/i)) || (navigator.userAgent.match(/iPad/i)) || (navigator.userAgent.match(/iPod/i))) {
		var wppizza_touch_pos;
		$(document).on('touchstart', '.wppizza-add-to-cart,.wppizza-add-ingredients', function(e) {
  			wppizza_touch_pos = $(window).scrollTop();
		}).on('touchend', '.wppizza-add-to-cart,.wppizza-add-ingredients', function(e) {
  			var scrollDistance=Math.abs(wppizza_touch_pos-$(window).scrollTop());
 				if(e.type=='touchend' && scrollDistance>3){ 
  				e.preventDefault();
 					return;
 				}
		});   		
	}
});